// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./LandStructs.sol";

contract LandModifiers {
    mapping(uint256 => Land) internal lands;
    mapping(address => bool) public verifiers;

    modifier onlyOwner(uint256 _landId) {
        bool isOwner = false;
        for(uint i = 0; i < lands[_landId].owners.length; i++) {
            if(lands[_landId].owners[i] == msg.sender) {
                isOwner = true;
                break;
            }
        }
        require(isOwner, "Not land owner");
        _;
    }

    modifier onlyVerifier() {
        require(verifiers[msg.sender], "Not authorized verifier");
        _;
    }

    function _initializeVerifier(address _verifier) internal {
        verifiers[_verifier] = true;
    }

    function _getLand(uint256 _landId) internal view returns (Land memory) {
        return lands[_landId];
    }
}
